import React from 'react';
import './App.css';
import { BrowserRouter as Router, Route, Link, Routes } from 'react-router-dom'; // Importa 'Routes'

import { ImagenProvider } from './ImagenContext';
import GaleriaImagenes from './GaleriaImagenes';
import GaleriaFavoritas from './GaleriaFavoritas';

function App() {
  return (
    <ImagenProvider>
      <Router>
        <div className="App">
          <h1>Aplicación de Galería</h1>
          <nav>
            <ul>
              <li>
                <Link to="/">Galería</Link>
              </li>
              <li>
                <Link to="/favoritos">Favoritos</Link>
              </li>
            </ul>
          </nav>
          <Routes> {/* Utiliza Routes en lugar de Switch */}
            <Route path="/" element={<GaleriaImagenes />} />
            <Route path="/favoritos" element={<GaleriaFavoritas />} />
          </Routes>
        </div>
      </Router>
    </ImagenProvider>
  );
}

export default App;
